INSERT INTO especialidade (nome,descricao) VALUES ('Alergia e Imunologia','diagnóstico e tratamento das doenças alérgicas e do sistema imunológico.');
INSERT INTO especialidade (nome,descricao) VALUES ('Anestesiologia','área da Medicina que envolve o tratamento da dor, a hipnose e o manejo intensivo do paciente sob intervenção cirúrgica ou procedimentos.');
INSERT INTO especialidade (nome,descricao) VALUES ('Angiologia','é a área da medicina que estuda o tratamento das doenças do aparelho circulatório.');
INSERT INTO especialidade (nome,descricao) VALUES ('Cancerologia (oncologia)','é a especialidade que trata dos tumores malignos ou câncer.');
INSERT INTO especialidade (nome,descricao) VALUES ('Cardiologia','aborda as doenças relacionadas com o coração e sistema vascular.');
INSERT INTO especialidade (nome,descricao) VALUES ('Cirurgia Cardiovascular','tratamento cirúrgico de doenças do coração.');
INSERT INTO especialidade (nome,descricao) VALUES ('Cirurgia da Mão','cuida das doenças das mãos e dos punhos, incluindo os ossos, articulações, tendões, músculos, nervos, vasos e pele.');
INSERT INTO especialidade (nome,descricao) VALUES ('Cirurgia de cabeça e pescoço','tratamento cirúrgico de doenças da cabeça e do pescoço.');
INSERT INTO especialidade (nome,descricao) VALUES ('Cirurgia do Aparelho Digestivo','tratamento clínico e cirúrgico dos órgãos do aparelho digestório, como o esôfago, estômago, intestinos, fígado e vias biliares, e pâncreas.');
INSERT INTO especialidade (nome,descricao) VALUES ('Cirurgia Geral','é a área que engloba todas as áreas cirúrgicas, sendo também subdividida.');
INSERT INTO especialidade (nome,descricao) VALUES ('Cirurgia Pediátrica','cirurgia geral em crianças.');
INSERT INTO especialidade (nome,descricao) VALUES ('Cirurgia Plástica','correção das deformidades, malformações ou lesões que comprometem funções dos órgãos através de cirurgia de caráter reparador ou cirurgias estéticas.');
INSERT INTO especialidade (nome,descricao) VALUES ('Cirurgia Torácica','atua na cirurgia da caixa torácica e vias aéreas.');
INSERT INTO especialidade (nome,descricao) VALUES ('Cirurgia Vascular','tratamento das veias e artérias, através de cirurgia, procedimentos endovasculares ou tratamentos clínicos.');
INSERT INTO especialidade (nome,descricao) VALUES ('Clínica Médica (Medicina interna) ','é a área que engloba todas as áreas não cirúrgicas, sendo subdividida em várias outras especialidades.');
INSERT INTO especialidade (nome,descricao) VALUES ('Coloproctologia','é a parte da medicina que estuda e trata os problemas do intestino grosso (cólon), sigmoide e doenças do reto, canal anal e ânus.');
INSERT INTO especialidade (nome,descricao) VALUES ('Dermatologia','é o estudo da pele anexos (pelos, glândulas), tratamento e prevenção das doenças.');
INSERT INTO especialidade (nome,descricao) VALUES ('Endocrinologia e Metabologia','é a área da Medicina responsável pelo cuidados aos hormônios, crescimento e glândulas como adrenal, tireoide, hipófise, pâncreas endócrino e outros.');
INSERT INTO especialidade (nome,descricao) VALUES ('Endoscopia','Esta especialidade médica ocupa-se do estudo dos mecanismo fisiopatológicos, diagnóstico e tratamento de enfermidades passíveis de abordagem por procedimentos endoscópicos e minimamente invasivos.');
INSERT INTO especialidade (nome,descricao) VALUES ('Gastroenterologia','é o estudo, diagnóstico, tratamento e prevenção de doenças relacionadas ao aparelho digestivo, desde erros inatos do metabolismo, doença do trato gastrointestinal, doenças do fígado e cânceres.');
INSERT INTO especialidade (nome,descricao) VALUES ('Genética médica','é a área da responsável pelo estudo das doenças genéticas humanas e aconselhamento genético.');
INSERT INTO especialidade (nome,descricao) VALUES ('Geriatria','é a sub-especialidade médica que cuida dos idosos e articula seu tratamento com outras especialidades.');
INSERT INTO especialidade (nome,descricao) VALUES ('Ginecologia e obstetrícia','é a especialidade médica que aborda de forma integral a mulher. Trata desde as doenças infecciosas sexuais, gestação, alterações hormonais, reprodução.');
INSERT INTO especialidade (nome,descricao) VALUES ('Hematologia e Hemoterapia','é o estudo dos elementos figurados do sangue (hemácias, leucócitos, plaquetas) e da produção desses elementos nos órgãos hematopoiéticos (medula óssea, baço, linfonódos), além de tratar das anemias, linfomas, leucemias e outros cânceres, hemofilia e doenças da coagulação');
INSERT INTO especialidade (nome,descricao) VALUES ('Homeopatia','é a prática médica pseudocientífica baseada na Lei dos Semelhantes. Esta é, por sua vez, uma pseudociência em consenso pela comunidade científica, já que apresenta provas científicas da sua não-eficácia. [2] [3] [4] [5] [6] [7] [8]');
INSERT INTO especialidade (nome,descricao) VALUES ('Infectologia','prevenção, diagnóstico e tratamentos de infecções causadas por vírus, bactérias, fungos e parasitas (helmintologia, protozoologia, entomologia e artropodologia).');
INSERT INTO especialidade (nome,descricao) VALUES ('Mastologia','sub-especialidade que trata da mama, suas doenças, alterações benignas e estéticas.');
INSERT INTO especialidade (nome,descricao) VALUES ('Medicina de Família e Comunidade','é a área da medicina que trata do indivíduo em seu ambiente familiar e comunitário, com foco na prevenção e tratamento das doenças mais comuns, sendo o articulador do encaminhamento aos especialistas quando necessária abordagem mais aprofundada das doenças.');
INSERT INTO especialidade (nome,descricao) VALUES ('Medicina de Emergência',' especialidade que atua no cuidado de pacientes com doenças ou lesões que requerem atenção médica imediata, atuando nas Emergências, pronto-atendimentos e serviços pré-hospitalares.');
INSERT INTO especialidade (nome,descricao) VALUES ('Medicina do Trabalho','trata do processo de trabalho e da relação deste com as doenças. Atua desde a prevenção dos agravos, a minimização dos efeitos destes e do tratamento das doenças do trabalho quando já estabelecidas.');
INSERT INTO especialidade (nome,descricao) VALUES ('Medicina do Tráfego','manutenção da saúde no indivíduo que se desloca, qualquer que seja o meio, cuidando das interações deste deslocamento com o indivíduo.');
INSERT INTO especialidade (nome,descricao) VALUES ('Medicina Esportiva','abordagem do atleta de uma forma global, desde a fisiologia do exercício à prevenção de lesões, passando pelo controle de treino e resolução de problemas de saúde que envolvam o praticante do exercício físico.');
INSERT INTO especialidade (nome,descricao) VALUES ('Medicina Física e Reabilitação','diagnóstico e terapêutica de diferentes entidades tais como doenças traumáticas, do sistema nervoso central e periférico, orto-traumatológica, cardiorrespiratória.');
INSERT INTO especialidade (nome,descricao) VALUES ('Medicina Intensiva','é o ramo da medicina que se ocupa dos cuidados dos doentes graves ou instáveis, que emprega maior número de recursos tecnológicos e humanos no tratamento de doenças ou complicações de doenças, congregando conhecimento da maioria das especialidades médicas e outras áreas de saúde.');
INSERT INTO especialidade (nome,descricao) VALUES ('Medicina Legal e Perícia Médica (ou medicina forense)','é a especialidade que aplica os conhecimentos médicos aos interesses da Justiça, na elaboração de leis e na adequada caracterização dos fenômenos biológicos que possam interessar às autoridades no sentido da aplicação das leis. Assim a Medicina Legal caracteriza a lesão corporal, a morte (sua causa, o momento em que ocorreu, que agente a produziu), a embriaguez pelo álcool ou pelas demais drogas, a violência sexual de qualquer natureza, etc.');
INSERT INTO especialidade (nome,descricao) VALUES ('Medicina Nuclear','é o estudo imaginológico ou terapia pelo uso de radiofármacos.');
INSERT INTO especialidade (nome,descricao) VALUES ('Medicina Preventiva e Social','se dedica especificamente à prevenção de doenças gerais (de várias áreas), porém não unicamente, já que cada área ou especialidade está também capacitada para tal.');
INSERT INTO especialidade (nome,descricao) VALUES ('Nefrologia','é a parte da medicina que estuda e trata clinicamente as doenças do rim, como insuficiência renal.');
INSERT INTO especialidade (nome,descricao) VALUES ('Neurocirurgia','atua no tratamento de doenças do sistema nervoso central e periférico passíveis de abordagem cirúrgica.');
INSERT INTO especialidade (nome,descricao) VALUES ('Neurologia','é a parte da medicina que estuda e trata o sistema nervoso.');
INSERT INTO especialidade (nome,descricao) VALUES ('Nutrologia','diagnóstico, prevenção e tratamento de doenças do comportamento alimentar.');
INSERT INTO especialidade (nome,descricao) VALUES ('Obstetrícia','é a área da medicina atrelada à Ginecologia que cuida das mulheres em relação ao processo da gestação (pré, pós-parto, puerpério, gestação e outros).');
INSERT INTO especialidade (nome,descricao) VALUES ('Oftalmologia','é a parte da medicina que estuda e trata os distúrbios dos olhos.');
INSERT INTO especialidade (nome,descricao) VALUES ('Ortopedia e Traumatologia','é a parte da medicina que estuda e trata as doenças do sistema osteomuscular, locomoção, crescimento, deformidades e as fraturas.');
INSERT INTO especialidade (nome,descricao) VALUES ('Otorrinolaringologia','é a parte da medicina que estuda e trata as doenças da orelha, nariz, seios paranasais, faringe e laringe.');
INSERT INTO especialidade (nome,descricao) VALUES ('Patologia','(também anatomia patológica ou patologia cirúrgica) é a especialidade que se ocupa da análise macroscópica, microscópica e molecular das doenças em autópsias, espécimes cirúrgicos, biópsias e preparados citológicos. Ela faz a ligação entre a ciência básica e a prática clínica.');
INSERT INTO especialidade (nome,descricao) VALUES ('Patologia Clínica/Medicina laboratorial','No Brasil, de forma geral é uma especialidade médica investigativa e atua como parte do processo diagnóstico das doenças.');
INSERT INTO especialidade (nome,descricao) VALUES ('Pediatria','é a parte da medicina que estuda e trata crianças.');
INSERT INTO especialidade (nome,descricao) VALUES ('Pneumologia','é a parte da medicina que estuda e trata o sistema respiratório.');
INSERT INTO especialidade (nome,descricao) VALUES ('Psiquiatria','é a parte da medicina que previne e trata ao transtornos mentais e comportamentais.');
INSERT INTO especialidade (nome,descricao) VALUES ('Radiologia e Diagnóstico por Imagem','realização e interpretação de exames de imagem como raio-X, ultrassonografia, Doppler colorido, Tomografia Computadorizada, Ressonância Magnética, entre outros.');
INSERT INTO especialidade (nome,descricao) VALUES ('Radioterapia','tratamento empregado em doenças várias, com o uso de raio X ou outra forma de energia radiante.');
INSERT INTO especialidade (nome,descricao) VALUES ('Reumatologia','é a especialidade médica que trata das doenças do tecido conjuntivo, articulações e doenças autoimunes. Diferente do senso comum o reumatologista não trata somente reumatismo.');
INSERT INTO especialidade (nome,descricao) VALUES ('Toxicologia médica','sub-especialidade da medicina com foco na toxicologia e fornece o diagnóstico, gerenciamento e prevenção de envenenamento e outros efeitos adversos devido a medicamentos, tóxicos ocupacionais e ambientais e agentes biológicos.');
INSERT INTO especialidade (nome,descricao) VALUES ('Urologia','é a parte da medicina que estuda e trata cirurgicamente e clinicamente os problemas do sistema urinário e do sistema reprodutor masculino e feminino.');
