 SELECT * FROM especialidade;
 SELECT * FROM paciente;
 SELECT * FROM medico;
 SELECT * FROM andar;
 SELECT * FROM quarto;
 SELECT * FROM telefone;
 SELECT * FROM plantao;
 SELECT * FROM ficha_atendimento;
 
 -- XXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXX
 
 SELECT f.id, p.nome as 'Paciente', t.nr_fone, q.nr_quarto,a.nr_andar,m.crm,m.nome,e.nome,f.hora_diaria_med_resp,pl.medId  
  FROM ficha_atendimento f
  INNER JOIN paciente p ON p.id = f.pacienteId
  INNER JOIN telefone t ON t.id = f.fonePacId
  INNER JOIN quarto q ON q.id = f.quartoId
  INNER JOIN andar a ON a.id = q.nrAndarId
  INNER JOIN medico m ON m.id = f.medId
  INNER JOIN especialidade e ON e.id = m.espId
  INNER JOIN plantao pl ON pl.id = f.plaId;
 